import React, {useEffect, useState} from 'react';
import {useParams, useNavigate} from "react-router-dom";
import axios from 'axios';

const Detalle = () => {
    const [petUnico, setPetUnico] = useState();
    const [likes, setLikes] = useState(0);

    console.log(petUnico)

    const { id } = useParams();

    const traerPorID = () => {
            axios.get(`http://localhost:8000/api/pet/${id}`)
            .then(res=>setPetUnico(res.data.user))
            .catch(err=>console.log("Error:", err))
    }

    useEffect(() => {
        id && traerPorID()
    }, [id]);


    const subirlikes =()=>{
        setLikes (likes + 1)
    }

    const navigate = useNavigate();

    const handleClick = () => {
        navigate ("/")
    };

    return (
        <div className="perros">
            <div className="detalleperros">
                <div></div>
                <h1>PET SHELTER</h1>
                {petUnico && <h2>Detalles sobre: {petUnico.name}</h2>}
            </div>

            <div className="cajadetalle">
                {petUnico && <h2>Descripci√≥n: {petUnico.petdescription}</h2>}
                {petUnico && <h2>Habilidades: {petUnico.skills}</h2>}
            </div>

            <div className="contador">
                {petUnico && <p  onClick= {subirlikes} className="botonback btn btn-primary mt-3" > Like {petUnico.name}</p>}
                <p>Likes {likes}</p>
            </div>

            <div>
            <div className="contenedorboton">
                <div><button className="botonback btn btn-primary mt-3" onClick={handleClick}>Back to home</button></div>
                </div>
            </div>
        </div>
    );
}

export default Detalle;
