import React from 'react';
import {Formik, Field, Form} from "formik"
import * as Yup from "yup";
import { useNavigate} from "react-router-dom";


const Petform = (props) => {
    const navigate = useNavigate();
    const {initialName, onSubmitProp, type, petDescription, skills} = props


    const handleClick = () => {
        navigate ("/")
    };

    return (
        <div>

        <div>
            <Formik
            initialValues = {{
                firstName: initialName,
                type: type, 
                petDescription: petDescription,
                skills: skills
            }}

            validationSchema = {Yup.object().shape ({
                firstName: Yup.string()
                .min(2, "Tu nombre es muy corto")
                .max(15,"Tu nombre es muy largo")
                .required("Obligatorio"),

                type: Yup.string()
                .min(2, "Tu type es muy corto")
                .max(15,"Tu type es muy largo")
                .required("Obligatorio"),

                petDescription: Yup.string()
                .min(2, "Tu type es muy corto")
                .max(100,"Tu type es muy largo")
                .required("Obligatorio"),

                skills1: Yup.string()
                .min(2, "Tu type es muy corto")
                .max(15,"Tu type es muy largo")
                .required("Obligatorio")
            })}

            onSubmit={(values,{setSubmitting})=>{
                onSubmitProp(values);
                console.log(values);
            }}

        >
            {({errors,
        touched,
        handleSubmit})=>{

            return (
                <div>
                    <div className= "formularioformik">
                        <h2>Formulario de registro</h2>
                        <Form onSubmit={handleSubmit}>
                            <label htmlFor='firstName'>Nombre de la mascota</label>
                            <Field id="firstName" type="text" placeholder="Nombre" name="firstName" className="form-control"/>
                            {errors.firstName && touched.firstName && <p className='error'>{errors.firstName} </p>}

                            <label htmlFor='type'>Tipo de mascota</label>
                            <Field id="ype" type="text" placeholder="Tipo" name="type" className="form-control"/>
                            {errors.type && touched.type && <p className='error'>{errors.type} </p>}

                            <label htmlFor='petDescription'>Descripción de la mascota</label>
                            <Field id="ype" petDescription="text" placeholder="Tipo" name="petDescription" className="form-control"/>
                            {errors.petDescription && touched.petDescription && <p className='error'>{errors.petDescription} </p>}

                            <label htmlFor='skills'>Habilidades</label>
                            <Field id="ype" skills="text" placeholder="Tipo" name="skills" className="form-control"/>
                            {errors.skills && touched.skills && <p className='error'>{errors.skills} </p>}

                            <button type="submit" disabled={Object.values(errors).length>0} className="btn btn-primary mt-3">Registrarse</button>
                            </Form>
                            <div className="contenedorboton">
                                <div><button className="botonback btn btn-primary mt-3" onClick={handleClick}>Back to home</button></div>
                            </div>
                    </div>
                </div>
            )
        }}
            </Formik>
        </div>


        </div>
    );
}

export default Petform;
